$(function () {
		
		function resizeBanner() {
			$('.hero').height($(window).height());
		}
		
		$(document).ready(function () {
		    resizeBanner();
		});
		
		$(window).resize(function () {
		    resizeBanner();
		});
		
		$(window).load(function(){
		    $('#cover').fadeOut(500);
		    
		});
		
		$('.portrait-container').bind('touchstart', function() {
		});
		
		$('.wrapper').css('top', $('header').outerHeight());
		
		$(".toggle-button").click(function() {
		    $('body').toggleClass('push');
		    $('#nav-icon3').toggleClass('open');
		});
		
		$('.desktop-menu a[href="#"], .sidebar a[href="#"], .mobile-nav a').click(function(e) {
		    e.preventDefault();
		});
		
		$('.nav-container li.menu-item-has-children').prepend('<span class="sub-toggle"><i class="fa fa-angle-down"></i></span>');
		$('span.sub-toggle').click(function (){
		  var myUL = $(this).siblings(".sub-menu").slideToggle();
		});
		$('span.sub-toggle').click(function (){
		$(this).children().toggleClass('fa-angle-down fa-angle-up');
		});
		$(".client-section-heading h3").wrapInner("<span></span>");
			
		//Slick slider
		$('.testimonials-slider').slick({
			 adaptiveHeight: true,
			 autoplay: false,
			 autoplaySpeed: 5000,
			 dots: true,
		});
		
		//Slick slider (logos)
		$('.client-logos-slider').slick({
		  dots: true,
		  arrows: true,
		  autoplay: true,
		  autoplaySpeed: 3000,
		  infinite: true,
		  speed: 600,
		  lazyLoad: 'ondemand',
		  slidesToShow: 4,
		  slidesToScroll: 4,
		  responsive: [
		    {
		      breakpoint: 1024,
		      settings: {
		        slidesToShow: 3,
		        slidesToScroll: 3,
		        infinite: true,
		        dots: true
		      }
		    },
		    {
		      breakpoint: 600,
		      settings: {
		        slidesToShow: 2,
		        slidesToScroll: 2
		      }
		    },
		    {
		      breakpoint: 480,
		      settings: {
		        slidesToShow: 1,
		        slidesToScroll: 1,
		        dots: false
		      }
		    }
		  ]
		});
		
		//Masonry
		var $grid = $('.grid').imagesLoaded( function() {
		$grid.masonry({
			itemSelector: '.grid-item',
			columnWidth: '.grid-sizer',
			percentPosition: true,
			gutter: '.gutter-sizer'
			});
		});
		
		$(window).resize(function() {
				$('.wrapper').css('top', $('header').outerHeight());
		});
		
		$(window).scroll(function() {    
		    var scroll = $(window).scrollTop();
		
		    if (scroll > 150) {
		    		$("header").addClass("shrink");
		        $(".desktop-menu li").addClass("shrink");
		        $(".desktop-menu a").addClass("shrink");
		        $(".logo img").addClass("shrink");
		    } else {
		    		$("header").removeClass("shrink");
		        $(".desktop-menu li").removeClass("shrink");
		        $(".desktop-menu a").removeClass("shrink");
		        $(".logo img").removeClass("shrink");
		    }
		});
		
		//Mobile menu tap fix
		$('.desktop-menu li.menu-item-has-children').on('touchstart', function (e) {
		    'use strict'; //satisfy code inspectors
		    var link = $(this); //preselect the link
		    if (link.hasClass('hover')) {
		        return true;
		    } else {
		        link.addClass('hover');
		        $(this).not(this).removeClass('hover');
		        e.preventDefault();
		        return false;
		    }
		});
		
		//SmoothScroll
		$(function() {
		  $('a[href*="#"]:not([href="#"])').click(function() {
		    if (location.pathname.replace(/^\//,'') == this.pathname.replace(/^\//,'') && location.hostname == this.hostname) {
		      var target = $(this.hash);
		      target = target.length ? target : $('[name=' + this.hash.slice(1) +']');
		      if (target.length) {
		        $('html, body').animate({
		          scrollTop: target.offset().top
		        }, 500);
		        return false;
		      }
		    }
		  });
		});

});

//Pointer Events for IE
function PointerEventsPolyfill(options) {
    this.options = {
        selector: '*',
        mouseEvents: ['click', 'dblclick', 'mousedown', 'mouseup'],
        usePolyfillIf: function() {
            if (navigator.appName == 'Microsoft Internet Explorer')
            {
                var agent = navigator.userAgent;
                if (agent.match(/MSIE ([0-9]{1,}[\.0-9]{0,})/) != null) {
                    var version = parseFloat(RegExp.$1);
                    if (version < 11)
                      return true;
                }
            }
            return false;
        }
    };
    if (options) {
        var obj = this;
        $.each(options, function(k, v) {
          obj.options[k] = v;
        });
    }

    if (this.options.usePolyfillIf())
      this.register_mouse_events();
}

PointerEventsPolyfill.initialize = function(options) {
    if (PointerEventsPolyfill.singleton == null)
      PointerEventsPolyfill.singleton = new PointerEventsPolyfill(options);
    return PointerEventsPolyfill.singleton;
};

PointerEventsPolyfill.prototype.register_mouse_events = function() {
    $(document).on(
        this.options.mouseEvents.join(' '),
        this.options.selector,
        function(e) {
        if ($(this).css('pointer-events') == 'none') {
             // peak at the element below
             var origDisplayAttribute = $(this).css('display');
             $(this).css('display', 'none');

             var underneathElem = document.elementFromPoint(
                e.clientX,
                e.clientY);

            if (origDisplayAttribute)
                $(this)
                    .css('display', origDisplayAttribute);
            else
                $(this).css('display', '');

            e.target = underneathElem;
            $(underneathElem).trigger(e);

            return false;
        }
        return true;
    });
};

